mapper.insert:
INSERT INTO member (email, name, nickname, pass, zipcode, address1, address2, job, phone, picture, role)
VALUES (#{email}, #{name}, #{nickname}, #{pass}, #{zipcode}, #{address1}, #{address2}, #{job}, #{phone}, #{picture},


        #{role}


        'user'


)

mapper.update:
UPDATE member
	SET pass=#{pass}, email=#{email}, phone=#{phone},
	name=#{name}, nickname=#{nickname}, zipcode=#{zipcode},
	address1=#{address1}, address2=#{address2}
	job=#{job}, phone=#{phone}, picture=#{picture}
WHERE email=#{email}
